                                   INSERT INTO
                                         vtabol.DISPONIBILIDAD
                                   (
                                         DISPONIBILIDAD_ID,
                                         CORRIDA_ID,
                                         FECCORRIDA,
                                         SECORIGEN,
                                         SECDESTINO,
                                         NUMASIENTO,
                                         CATEGORIA_ID,
                                         STATUSASIENTO,
                                         ACTIVO,
                                         FECMODIF,
                                         USUARIO_ID
                                   )
                                   VALUES
                                   (
                                         vtabol.DISPONIBILIDAD_SEQ.NEXTVAL,
                                         :NEW.CORRIDA_ID,
                                         :NEW.FECCORRIDA,
                                         varSecOrigen,
                                         varSecDestino,
                                         :NEW.NUMASIENTO,
                                         :NEW.CATEGORIA_ID,
                                         constStatusAsiento,
                                         :NEW.ACTIVO,
                                         :NEW.FECMODIF,
                                         :NEW.USUARIO_ID
                                   );
                             EXCEPTION
                                   WHEN OTHERS THEN
                                         DBMS_OUTPUT.PUT_LINE (
                                               'Fracaso la insercion del registro. '
                                               || SQLERRM || ' id:' || :NEW.BOLETO_ID);
                             END;
                  END;
            ELSE
                  DELETE
                  FROM
                        vtabol.DISPONIBILIDAD
              WHERE
                        corrida_id = :NEW.CORRIDA_ID AND
                        fecCorrida = :NEW.FECCORRIDA AND
                        secOrigen = varSecOrigen AND
                        secDestino = varSecDestino AND
                        numAsiento = :NEW.NUMASIENTO AND
                  activo = constActivo AND
                  statusAsiento = constStatusAsiento;
            END IF;
      END IF;
EXCEPTION
      WHEN OTHERS THEN
            DBMS_OUTPUT.PUT_LINE ('Fracaso TRIGGER Tr_Upd_Disponibilidad_Boleto ' || SQLERRM || ' i');
END Tr_Upd_Disponibilidad_Boleto;
