      DBMS_LOB.APPEND (res, '</osdm_s_dt:NotesDetails>');
      DBMS_LOB.APPEND (res, '</osdm_s_dt:Notes>');
      DBMS_LOB.APPEND (res, '</osdm_s_dt:DescriptionNotes>');
   
