--------------------------------------------------------
--  DDL for Procedure RESET_SEQUENCE
--------------------------------------------------------
set define off;

  CREATE OR REPLACE PROCEDURE "VTABOL"."RESET_SEQUENCE" 
(p_seq_name IN VARCHAR2, p_val IN number)
AS
 l_current number := 0;
  l_difference number := 0;
  l_minvalue user_sequences.min_value%type := 0;

begin

  select min_value
  into l_minvalue
  from user_sequences
  where sequence_name = p_seq_name;

  execute immediate
  'select ' || p_seq_name || '.nextval from dual' INTO l_current;

  if p_Val < l_minvalue then
    l_difference := l_minvalue - l_current;
  else
    l_difference := p_Val - l_current;
  end if;

  if l_difference = 0 then
    return;
  end if;

  execute immediate
    'alter sequence ' || p_seq_name || ' increment by ' || l_difference ||
       ' minvalue ' || l_minvalue;

  execute immediate
    'select ' || p_seq_name || '.nextval from dual' INTO l_difference;

  execute immediate
    'alter sequence ' || p_seq_name || ' increment by 1 minvalue ' || l_minvalue;
end;

/

  GRANT EXECUTE, DEBUG ON "VTABOL"."RESET_SEQUENCE" TO "VTABOL_TRC";
